DataGrid.ClientEvent:
var currentData = view.get("#dgServiceOrder").getCurrentItem();

if (currentData){
	var orderStatus = currentData.get("orderStatus");
	var startBtn = view.get("#startBtn");
	var modifyBtn = view.get("#modifyBtn");
	var deleteBtn = view.get("#deleteBtn");
	if (orderStatus == '10'){
		startBtn.set("disabled",false);
		modifyBtn.set("disabled",false);
		deleteBtn.set("disabled",false);
	}else {
		startBtn.set("disabled",true);
		modifyBtn.set("disabled",true);
		deleteBtn.set("disabled",true);
	}
}

